mapper.update:
update dish d set
                  d.status = #{status}
where d.id = #{id}

mapper.select:
select d.*, c.name categoryName from dish d inner join category c on d.category_id = c.id


    and d.name like concat('%', #{name}, '%')


    and d.status = #{status}


    and d.category_id = #{categoryId}


order by d.create_time desc
