mapper.update:
update setmeal set
                   setmeal.status = #{status},
                   setmeal.update_user = #{updateUser},
                   setmeal.update_time = #{updateTime}
               where setmeal.id = #{id}

mapper.select:
select * from setmeal s


        and s.name like concat('%', #{name}, '%')


        and s.status = #{status}


        and s.category_id = #{categoryId}


order by s.create_time desc
