mapper.update:
update shopping_cart set shopping_cart.number = #{number}


                                                            and dish_id = #{dishId}


                                                            and setmeal_id = #{setmealId}


                                                            and dish_flavor = #{dishFlavor}


                                                            and user_id = #{userId}

mapper.select:
select * from shopping_cart 

    and dish_id = #{dishId}


    and setmeal_id = #{setmealId}


    and dish_flavor = #{dishFlavor}


    and user_id = #{userId}
